    return(
        <div className="card">

            <img src={image} className="image"></img>

            <div className="tour-info">
            <div className="tour-details">
                <h4 className="tour-price">{price}</h4>
                <h4 className="tour-name">{name}</h4>
            </div>

            <div className="description">
                {description}
                <span></span>
            </div>
            </div>

            <button>Not Interested</button>


        </div>
    )
}
export default Card;
